data.logic:
var low_risk_factor = low_risk_factor || 0;
var rotate_neck_45 = rotate_neck_45 || 0;
var calc_output = [];
var msg = '';
var mini_msg = '';
var txt = '';
var age_65 = parseFloat(age_65);
low_risk_factor = parseFloat(low_risk_factor);
rotate_neck_45 = parseFloat(rotate_neck_45);
if (age_65 === 2) {
    msg = 'High risk patient. C-spine cannot be cleared clinically by these criteria. Imaging is recommended.';
    mini_msg = 'High';
    txt = 'Imaging recommended';
} else if (age_65 === 1) {
    msg = 'The Canadian C-Spine rule requires more information to provide a recommendation. Answer the next criteria.';
    mini_msg = 'Unknown';
    txt = ''
    if (low_risk_factor === 2) {
        if (rotate_neck_45 === 2) {
            msg = 'C-spine can be cleared clinically by these criteria. No imaging is required.';
            mini_msg = 'Low';
        } else if (rotate_neck_45 === 1) {
            msg = 'High risk patient due to inability to rotate neck. C-spine cannot be cleared clinically by these criteria. Imaging is recommended.';
            mini_msg = 'High';
        }
    } else if (low_risk_factor === 1) {
        msg = 'High risk patient due to lack of low-risk criteria. C-spine cannot be cleared clinically by these criteria. Imaging is recommended.';
        mini_msg = 'High';
    }
}
calc_output.push({
    name: 'mini',
    value: mini_msg,
    value_text: 'risk',
    message: txt
});
calc_output.push({
    name: '696_Canadian C-Spine Rule_result',
    value: mini_msg,
    value_text: 'risk',
    message: msg
});
