data.logic:
var calc_output = [];
var total_cholesterol = parseFloat(total_cholesterol);
var hdl_cholesterol = parseFloat(hdl_cholesterol);
var triglycerides = parseFloat(triglycerides);
if (UOMSYSTEM == false) { // convert these puppies to SI units for the equation
  total_cholesterol = total_cholesterol / 0.0259;
  hdl_cholesterol = hdl_cholesterol / 0.0259;
  triglycerides = triglycerides / 0.0113;
}
var v = total_cholesterol - hdl_cholesterol - (triglycerides / 5);
var unit = '';
if (UOMSYSTEM == false) { // convert the result back into mmol/L for cholesterols
  v = v * 0.0259;
  unit = 'mmol/L';
} else {
  unit ='mg/dL'
}

var risk;
if (v.toFixed(1) < 100) {
	risk = 'Optimal'
}
if (v.toFixed(1) >= 100 && v.toFixed(1) < 130) {
	risk = 'Near optimal'
}
if (v.toFixed(1) >= 130 && v.toFixed(1) < 160) {
	risk = 'Borderline high'
}
if (v.toFixed(1) >= 160 && v.toFixed(1) < 190) {
	risk = 'High'
}
if (v.toFixed(1) >= 190) {
	risk = 'Very high'
}

calc_output.push({
  name: 'mini',
  value: v.toFixed(1),
  value_text: unit,
  message: 'Calculated LDL level'
});
calc_output.push({
  name: '70_LDL Calculated_result',
  value: v.toFixed(1),
  value_text: unit,
  message: 'Calculated LDL level<br/><br/>' + risk + ' LDL level according to ATP III guidelines. See Evidence for cutoffs and target LDL levels according to risk.'
});
