data.logic:
var calc_output = [];

var result, msg, rec

if (parseFloat(ischemic) + parseFloat(arteries) + parseFloat(embolic) + parseFloat(other) == 4) {
	result = 'Positive'
  	msg = 'ESUS criteria fulfilled'
  	rec = '. Further evaluation indicated (see Next Steps for details)'
}
else {
  	result = 'Negative'
   	msg = 'ESUS criteria not fulfilled'
  	rec = ''
}

calc_output.push({
   name: 'mini',
   value: result,
   value_text: '',
   message: msg
});
calc_output.push({
   name: '10007_ESUS Criteria_result',
   value: result,
   value_text: '',
   message: msg + rec
});
