var calc_output = [];
var weight;

if (UOMSYSTEM == false) {
	weight = parseFloat(weight);
}
if (UOMSYSTEM == true) {
	weight = parseFloat(weight)*0.453592;
}

dose = parseFloat(dose) || 0;

var max_dose = weight * 5;

var dose_by_weight = dose / weight;

dose_by_weight = dose_by_weight.toFixed(1);

var msg
if (dose_by_weight > 5) {
  msg = 'Warning: this is greater than the recommended daily dose of ≤5 mg/kg. Consider lowering dose.'
}
else {
  msg = 'Dose is within recommended daily dose of ≤5 mg/kg.';
}

var risk1;
var risk2;

if(dose_by_weight >= 4 && dose_by_weight <= 5){
  risk1 = 2;
  risk2 = '20%';
}
else if(dose_by_weight > 5){
  risk1 = 10;
  risk2 = '40%';
}
//results
calc_output.push({
    name: 'mini',
    value: max_dose.toFixed(0),
    value_text: 'mg',
    message: 'Max daily hydroxychloroquine dose'
});
calc_output.push({
    name: '10080_Hydroxychloroquine (Plaquenil) Dosing_result (max)',
    value: max_dose.toFixed(0),
    value_text: 'mg daily dose',
    message: 'Maximum daily dose of hydroxychloroquine'
});

if (dose != 0) {
calc_output.push({
    name: '10080_Hydroxychloroquine (Plaquenil) Dosing_result (dose/weight)',
    value: dose_by_weight,
    value_text: 'mg/kg',
    message: msg
});}
if (dose_by_weight >= 4){
calc_output.push({
    name: '10080_Hydroxychloroquine (Plaquenil) Dosing_result (risk)',
    value: risk1,
    value_text: '%',
    message: '10-year risk of retinopathy ('+ risk2 + ' risk at 20 years)'
});
}
