data.logic:
var calc_output = [];
var wt = parseFloat(weight);
if (UOMSYSTEM == true) {
	wt = wt * 0.45454545
}
var u;
if (wt <= 10) { u = wt * 4 }
if (wt > 10 && wt <= 20) { u = 40 + ((wt - 10) * 2) }
if (wt > 20) { u = 60 + ((wt - 20) * 1) } 

calc_output.push({ 
	name: 'mini', 
	value: u.toFixed(0), 
	value_text: 'mL/hr', 
	message: 'Maintenance fluids' 
});
calc_output.push({ 
	name: '72_Maintenance Fluids_result (fluids)', 
	value: u.toFixed(0), 
	value_text: 'mL/hr', 
	message: 'Maintenance fluids' 
});
calc_output.push({ 
	name: '72_Maintenance Fluids_result (fluids x 1.5)', 
	value: (u * 1.5).toFixed(0), 
	value_text: 'mL/hr', 
	message: 'Maintenance fluids x 1.5' 
});
calc_output.push({ 
	name: '72_Maintenance Fluids_result (bolus)', 
	value: (wt * 20).toFixed(0), 
	value_text: 'mL', 
	message: 'Fluid bolus (20 mL/kg)' 
});
