data.logic:
var calc_output = [];
var age, cr, black_race, sex;
age = parseFloat(age);
cr = parseFloat(creatinine);
black_race = parseFloat(black_race) || -1;
sex = parseFloat(sex);
if (UOMSYSTEM == false){
  cr = cr/88.4;
}
var v = 175 * Math.pow(age, -0.203) * Math.pow(cr, -1.154);

if (sex == 1) { v = v * 0.742;}

if (black_race == 1){
  v = v * 1.212;
}

if (black_race == 1 || black_race == 2) {
  calc_output.push({ 
    name: 'mini', 
    value: v.toFixed(1), 
    value_text: 'mL/min/1.73 m\u00B2', 
    message: 'GFR' 
  });
  calc_output.push({ 
    name: '76_MDRD GFR Equation_result', 
    value: v.toFixed(1), 
    value_text: 'mL/min/1.73 m\u00B2', 
    message: 'Glomerular Filtration Rate by the MDRD Equation' 
  });
}
if (black_race == -1) {
	var u = v * 1.212;

calc_output.push({ 
  name: 'mini', 
  value: v.toFixed(1)+'-'+u.toFixed(1), 
  value_text: 'mL/min/1.73 m\u00B2', 
  message: 'GFR' 
});

calc_output.push({ 
  name: '76_MDRD GFR Equation_result', 
  value: v.toFixed(1)+'-'+u.toFixed(1), 
  value_text: 'mL/min/1.73 m\u00B2', 
  message: 'Glomerular Filtration Rate by the MDRD Equation; range provided as race not selected' 
});
}
