data.logic:
var calc_output=[]; 

var v,valuepercent,valueround,msg,round,mini_msg;

v=1/(parseFloat(source)*parseFloat(innoculum)*parseFloat(transmission));
v*=parseFloat(volume);
valuepercent=100*v;
valuepercent=Math.round(valuepercent*1000)/1000;

if(valuepercent<0.0001){valuepercent='<<0.001'};
valueround=1/v;valueround=Math.round(valueround);
round=0;
if(valueround<=1000){msg='Or, 1 in '+valueround+'. PEP treatment definitely indicated, according to the article.';
round=1;
mini_msg='PEP treatment definitely indicated.'} 
if(valueround>1000&&valueround<=10000){msg='Or, 1 in '+valueround+'. PEP treatment recommended, but optional, according to the article.';
round=2;
mini_msg='PEP treatment recommended.'} 
if(valueround>10000&&valueround<=100000){msg='Or, 1 in '+valueround+'. PEP treatment optional but not recommended, according to the article.';
round=3;
mini_msg='PEP treatment optional.'} 
if(valueround>100000){msg='Or, 1 in '+valueround+'. PEP treatment not indicated, according to the article.';
round=4;
mini_msg='PEP treatment not indicated.'} 

if(valuepercent=='<<0.001'){ 
    calc_output.push({
        name:'mini',
        value:valuepercent,
        value_text:'%',
        message:mini_msg
    }); 
    calc_output.push({
        name:'780_HIV Needle Stick RASP_result',
        value:valuepercent,
        value_text:'%',
        message:msg
    }); 
} else{ 
    calc_output.push({
        name:'mini',
        value:valuepercent.toFixed(round),
        value_text:'%',
        message:mini_msg
    }); 
    calc_output.push({
        name:'780_HIV Needle Stick RASP_result',
        value:valuepercent.toFixed(round),
        value_text:'%',
        message:msg
    });  
}
