data.logic:
var calc_output = [];
//must declare vars
var serum_protein = parseFloat(serum_protein);
var pleural_protein = parseFloat(pleural_protein);
var serum_ldh = parseFloat(serum_ldh);
var upper_limit_serum_ldh = parseFloat(upper_limit_serum_ldh);
var pleural_ldh = parseFloat(pleural_ldh);
var valuea = 0;
var valueb = 0;
var valuec = 0;
var done = 0;
var msg = '';
if (UOMSYSTEM == false) {
    serum_protein = serum_protein*10;
    pleural_protein = pleural_protein*10;
    //other inputs are 1 to 1 conversions
}
if (pleural_protein && serum_protein) {
    done = 1;
    if ((pleural_protein / serum_protein) > 0.5) { valuea = 1;} else { valuea = 0;}
}
if (pleural_ldh && serum_ldh) {
    done = 1;
    if ((pleural_ldh / serum_ldh) > 0.6) { valueb = 1;} else { valueb = 0;}
}
if (pleural_ldh && upper_limit_serum_ldh) {
    done = 1;
    if (pleural_ldh > (2 / 3) * upper_limit_serum_ldh) { valuec = 1;} else { valuec = 0;}
}
if (done == 1) {
    if (valuea == 1 || valueb == 1 || valuec == 1) {
        msg = 'At least one of Light\u2019s Criteria has been met; according to one study, this was 98% sensitive for exudative effusion.';
        calc_output.push({ 
            name: 'mini', 
            value: ' ', 
            value_text: ' ', 
            message: 'Positive Light\u2019s Criteria' 
        });
        calc_output.push({ 
            name: "797_Light's Criteria_result", 
            value: ' ', 
            value_text: ' ', 
            message: msg 
        });
    } else {
        msg = 'None of Light\u2019s criteria met; suggests likely transudative effusion.';
        calc_output.push({ name: 'mini', value: ' ', value_text: ' ', message: 'None of the Light\u2019s Criteria were met.' });
        calc_output.push({ name: "797_Light's Criteria_result", value: ' ', value_text: ' ', message: msg });
    }
}
else {
        msg = 'None of Light\u2019s criteria met; suggests likely transudative effusion.';
        calc_output.push({ name: 'mini', value: 'Result:', value_text: ' ', message: 'Please enter values to begin calculation.' });
        calc_output.push({ name: "797_Light's Criteria_result", value: 'Result:', value_text: ' ', message: 'Please enter values to begin calculation.' });
}
