data.logic:
var calc_output = [];
var u = parseFloat(sex) + parseFloat(age) + parseFloat(heart_failure) + parseFloat(hypertension) + parseFloat(stroke) + parseFloat(vascular_disease) + parseFloat(diabetes);
var msg = '';
var mini_msg = '';
var webLanguage = webLanguage || "english"

if (webLanguage == "webProduction-chinese-traditional") {
    var msgalt = '0分基本上為低風險，也不一定需要抗凝血劑使用；1分是低到中等風險，且應考慮抗血小板藥物或抗凝血劑使用；2分或以上屬於中等到高風險，且應考慮抗凝血劑使用。';
} else if (webLanguage == "webProduction-chinese-simplified") {
    var msgalt = '建议：0分 低风险，可能不需要使用抗凝剂；1分 低中度风险，应考虑使用抗血小板药物或抗凝剂；2分以上 中高度风险，应该使用抗凝剂。';
} else if (webLanguage == "webProduction-spanish") {
    var msgalt = 'Una recomendación sugiere que 0 puntos es “bajo” riesgo y tal vez no requiere anticoagulación; puntaje de uno es “bajo-moderado” riesgo y debería considerar antiplaquetarios o anticoagulantes, y puntaje de 2 o más es “moderado-alto” riesgo y de lo contrario debe ser candidato para anticoagulación. ';
} else {
    var msgalt = 'One recommendation suggests a 0 score for men or 1 score for women (no clinical risk factors) is “low” risk and may not require anticoagulation; a 1 score for men or 2 score for women is “low-moderate” risk and should consider antiplatelet or anticoagulation; and a score ≥2 for men or ≥3 for women is “moderate-high” risk and should otherwise be an anticoagulation candidate.';
}

var calculations = {
    "0": {
        stroke_risk_percentage: "0.2%",
        stroke_tia_systemic_percentage: "0.3%"
    },
    "1": {
        stroke_risk_percentage: "0.6%",
        stroke_tia_systemic_percentage: "0.9%"
    },
    "2": {
        stroke_risk_percentage: "2.2%",
        stroke_tia_systemic_percentage: "2.9%"
    },
    "3": {
        stroke_risk_percentage: "3.2%",
        stroke_tia_systemic_percentage: "4.6%"
    },
    "4": {
        stroke_risk_percentage: "4.8%",
        stroke_tia_systemic_percentage: "6.7%"
    },
    "5": {
        stroke_risk_percentage: "7.2%",
        stroke_tia_systemic_percentage: "10.0%"
    },
    "6": {
        stroke_risk_percentage: "9.7%",
        stroke_tia_systemic_percentage: "13.6%"
    },
    "7": {
        stroke_risk_percentage: "11.2%",
        stroke_tia_systemic_percentage: "15.7%"
    },
    "8": {
        stroke_risk_percentage: "10.8%",
        stroke_tia_systemic_percentage: "15.2%"
    }
}

var stroke_risk_percentage = calculations[u] ? calculations[u].stroke_risk_percentage : "12.2%"

var stroke_tia_systemic_percentage = calculations[u] ? calculations[u].stroke_tia_systemic_percentage : "17.4%"



if (webLanguage == "webProduction-chinese-traditional") {

    msg = '中風機會是每年' + stroke_risk_percentage + '大於九萬人( 來自 瑞士的心房顫動世代研究) 和' + stroke_tia_systemic_percentage + '的中風/暫時性中風/系統性血栓形成。';

    if (u == 8) {
        msg += '<br/><br/>在這篇文章，我們發現八分比7分的風險還要低，顯然的我們應該要假設所有大於7分的患者風險都大於10%'
    }

    mini_msg = stroke_risk_percentage + ' Stroke risk per year.'

} else if (webLanguage == "webProduction-chinese-simplified") {

    msg = '在90000多名病人中，中风风险为每年' + stroke_risk_percentage + '（来自瑞士的一个关于房颤的队列研究），中风/短暂性脑缺血/系统性血栓形成风险为' + stroke_tia_systemic_percentage + "。";

    if (u == 8) {
        msg += '<br/><br/>在这个研究中，我们发现8分比7分的风险还要低，显然，我们应该假设所有大于7分的患者风险都大于10%。'
    }

    mini_msg = stroke_risk_percentage + ' Stroke risk per year.'


} else if (webLanguage == "webProduction-spanish") {


    msg = 'El riesgo de derrame fue de ' + stroke_risk_percentage + '(estudio sueco de cohorte) y ' + stroke_tia_systemic_percentage + ' riesgo de derrame/AIT/embolismo sistémico.';

    if (u == 8) {
        msg += '<br/><br/>Nosotros realizamos que 8 puntos demuestran menos riesgo que 7 puntos, estos fueron las recomendaciones en el estudio, obviamente deberá asumir todos puntajes > 7 tienen un riesgo > 10%.'
    }

    mini_msg = stroke_risk_percentage + ' Stroke risk per year.'


} else {
    msg = 'Stroke risk was ' + stroke_risk_percentage + ' per year in >90,000 patients (the Swedish Atrial Fibrillation Cohort Study) and ' + stroke_tia_systemic_percentage + ' risk of stroke/TIA/systemic embolism.';

    if (u == 8) {
        msg += '<br/><br/>We realize that 8 points showed a lower risk than 7 points, these were the findings in the study, obviously one should assume all scores \u22657 have a risk >10%.'
    }

    mini_msg = stroke_risk_percentage + ' Stroke risk per year.'
}

var value_text = ''

if (webLanguage == "webProduction-chinese-traditional" || webLanguage == "webProduction-chinese-simplified") {
    var value_text = '分'
} else if (webLanguage == "webProduction-spanish") {
    if (u.toFixed(0) == 1) {
        var value_text = 'punto'
    } else {
        var value_text = 'puntage'
    }
} else {
    var value_text = 'points'
}

calc_output.push({
    name: 'mini',
    value: u.toFixed(0),
    value_text: value_text,
    message: mini_msg
});
calc_output.push({
    name: '801_CHA2DS2-VASc Score_result',
    value: u.toFixed(0),
    value_text: value_text,
    message: msg + '<br/><br/>' + msgalt
});
