data.logic:
var calc_output=[]; 
var msg,recs=''; 
var mini_msg=''; 
var u=parseInt(hypertension)+parseInt(renal_disease)+parseInt(liver_disease)+parseInt(stroke)+parseInt(bleeding_predisposition)+parseInt(labile_inr)+parseInt(age_65)+parseInt(medication_usage)+parseInt(alcohol_drug_use); 
if(u==0){
	msg='Risk was 0.9% in one validation study (Lip 2011) and 1.13 bleeds per 100 patient-years in another validation study (Pisters 2010).';
	recs='Anticoagulation should be considered: Patient has a relatively low risk for major bleeding (~1/100 patient-years).';
	mini_msg='Relatively low risk of major bleeding.'
} 
if(u==1){
	msg='Risk was 3.4% in one validation study (Lip 2011) and 1.02 bleeds per 100 patient-years in another validation study (Pisters 2010).';
	recs='Anticoagulation should be considered: Patient has a relatively low risk for major bleeding (~1/100 patient-years).';
	mini_msg='Relatively low risk of major bleeding.'
} 
if(u==2){
	msg='Risk was 4.1% in one validation study (Lip 2011) and 1.88 bleeds per 100 patient-years in another validation study (Pisters 2010).';
	recs='Anticoagulation can be considered, however patient does have moderate risk for major bleeding (~2/100 patient-years).';
	mini_msg='Moderate risk of major bleeding.'
} 
if(u>2){
	recs='Alternatives to anticoagulation should be considered: Patient is at high risk for major bleeding.'
}  
if(u==3){
	msg='Risk was 5.8% in one validation study (Lip 2011) and 3.72 bleeds per 100 patient-years in another validation study (Pisters 2010).';
	mini_msg='High risk of major bleeding.'
} 
if(u==4){
	msg='Risk was 8.9% in one validation study (Lip 2011) and 8.70 bleeds per 100 patient-years in another validation study (Pisters 2010).';
	mini_msg='High risk of major bleeding.'
} 
if(u==5){
	msg='Risk was 9.1% in one validation study (Lip 2011) and 12.50 bleeds per 100 patient-years in another validation study (Pisters 2010).';
	mini_msg='High risk of major bleeding.'
} 
if(u>5){
	msg='Scores greater than 5 were too rare to determine risk, but are likely over 10%.';
	mini_msg='Very high risk of major bleeding.'
	recs='Alternatives to anticoagulation should be considered: Patient is at very high risk for major bleeding.'} 

calc_output.push({
	name:'mini',
	value:u,
	value_text:'points',
	message:mini_msg
}); 
calc_output.push({
	name:'807_HAS-BLED Score_result',
	value:u,
	value_text:'points',
	message:msg+'<br/><br/>'+recs
});
