data.logic:
var calc_output = [];
var bili = parseFloat(bilirubin);
var alb = parseFloat(albumin);
var inr = parseFloat(inr);
var v = 0;
var msg = '';
var growth, infant;
var wlsurv = '';
var ptsurv = '';
if (UOMSYSTEM == false) {
  bili = bili/17.104;
  alb = alb/10;
  //only inputs that need conversion
}
if (growth_failure == 1) { growth = 0.667 } else { growth = 0 }
if (parseFloat(age) > 11) { 
    calc_output.push({ 
        name: 'mini', 
        value: ' ', 
        value_text: ' ', 
        message: 'Please use MELD' 
    });
    calc_output.push({ 
        name: '87_PELD Score_result', 
        value: ' ', 
        value_text: ' ', 
        message: 'For patients 12 and older, please use the MELD Calculator. PELD is for patients less than 12 years old.' 
    }); 
} else {
    if (age < 2) { 
        infant = .436;
        msg = '<br/><br/>Note: For children under 1 year already listed for transplant, their PELD score should be calculated using an age of 1 until they reach 2 years of age. (The age of 1 adds points to the PELD score.)' } else { infant = 0 }
    v = 10 * ((.48 * Math.log(bili)) + (1.857 * Math.log(inr)) - (.687 * Math.log(alb)) + infant + growth);
    if (v < 0){
        v = 0;
    } else {
        v = v;
    }
    if (v <= 6) { wlsurv = '86.8%';
        ptsurv = '93.6%' }
    if (v >= 7 && v <= 16) { wlsurv = '76.3%';
        ptsurv = '90.9%' }
    if (v >= 17 && v <= 27) { wlsurv = '65.5%';
        ptsurv = '89.4%' }
    if (v >= 28) { wlsurv = '66.1%';
        ptsurv = '84.6%' }
    calc_output.push({
        name: 'mini', 
        value: v.toFixed(1), 
        value_text: 'points', 
        message: 'PELD Score' 
    });
    calc_output.push({ 
        name: '87_PELD Score_result (score)', 
        value: v.toFixed(1), 
        value_text: 'points', 
        message: 'PELD Score' + msg 
    });
    calc_output.push({ 
        name: '87_PELD Score_result (waiting list)', 
        value: wlsurv, 
        value_text: ' ', 
        message: '1-Year Waiting List Survival'
    });
    calc_output.push({ 
        name: '87_PELD Score_result (posttransplant)', 
        value: ptsurv, 
        value_text: ' ', 
        message: '1-Year Posttransplant Survival' 
    }); 
}
