data.logic:
var calc_output = [];
var sodium = parseFloat(sodium);
var bun = parseFloat(bun);
var glucose = parseFloat(glucose);
var ethanol = parseFloat(ethanol);
if (UOMSYSTEM == false) {
  //sodium is 1:1 conversion, does not need UOM conversion
  bun = bun / 0.357;
  glucose = glucose / 0.0555;
  ethanol = ethanol / 0.217;
}

if (ethanol) {

  var va = 2 * (sodium) + (bun / 2.8) + (glucose / 18) + (ethanol / 4.6);
  var vb = 2 * (sodium) + (bun / 2.8) + (glucose / 18) + (ethanol / 3.7);

  if (UOMSYSTEM == false) {
    var v = '~' + va.toFixed();
  } else {

    var v = va.toFixed(0).toString() + '-' + vb.toFixed(0).toString();
  }

} else {
  var v = 2 * (sodium) + (bun / 2.8) + (glucose / 18);
  v = v.toFixed(0);
  va = v;
}

calc_output.push({
  name: 'mini',
  value: v,
  value_text: 'mOsm/kg' + ' Calculated Serum Osm',
  message: 'Normal serum osmolality = 285-295 mOsm/kg'
});
calc_output.push({
  name: '91_Serum Osmolality_result (osm)',
  value: v,
  value_text: 'mOsm/kg' + ' Calculated Serum Osm',
  message: 'Normal serum osmolality = 285-295 mOsm/kg'
});
if (measured_serum) {
  var diff = parseFloat(measured_serum) - va;
  if (ethanol) {
   diff = parseFloat(measured_serum) - vb; 
  }
  var msg = 'Normal Serum Osm Gap (Measured-Calculated) is -14 to +10.';
  if (diff > 25) {
    msg =
      'This is a significant Osm Gap; if there is also an anion gap acidosis, you should strongly consider methanol or ethylene glycol intoxication.<br/><br/>Other causes of Osmolal Gap include other alcohols (acetone, isopropyl alcohol, polyethylene or propylene glycol), sugars (mannitol, sorbitol), lipids (hypertriglyceridemia) or proteins (hypergammaglobulinemia).';
  }
  calc_output.push({
    name: '91_Serum Osmolality_result (gap)',
    value: diff.toFixed(1),
    value_text: 'mOsm/kg' + ' Osm Gap',
    message: msg
  });
}
