data.logic:
var calc_output = [];

var who = parseFloat(who);
var karyotype = parseFloat(karyotype);
var transfusion = parseFloat(transfusion);

var score = who + karyotype + transfusion;

var risk = ''
var survival = ''

if (score == 0) {
	risk = 'Very low';
	survival = '141';
}
else if (score == 1) {
	risk = 'Low';
	survival = '66';
}
else if (score == 2) {
	risk = 'Intermediate';
	survival = '48';
}
else if (score == 3 || score == 4) {
	risk = 'High';
	survival = '26';
}
else {
	risk = 'Very high';
	survival = '9';
}

//results 
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: ' risk',
    message: survival + ' months median survival'
    });
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'WPSS for MDS'
    });
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '',
    message: 'Risk group'  
    });
calc_output.push({
    name: 'survival',
    value: survival,
    value_text: 'months',
    message: 'Median survival'
    });
