data.logic:
var calc_output = [];

var morph = parseFloat(morph);
var dis = parseFloat(dis);
var neuro = parseFloat(neuro);

if (neuro > 0) {
  var cord = parseFloat(cord);
}
else {
  var cord = 0
  }

var score = morph + dis + neuro + cord; 

var recommend;

if (score < 4) {
	recommend = 'Non-op';
}
else if (score == 4) {
	recommend = 'Non-op or operative';
}
else {
	recommend = 'Operative';
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: recommend + ' treatment'
    });
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'SLICS Score'
    });

if (score >= 5) {
	calc_output.push({
    	name: 'recommend',
    	value: recommend,
    	value_text: 'treatment',
    	message: 'May consist of realignment, neurological decompression (if indicated), and stabilization'
    });
}
else {
  calc_output.push({
    name: 'recommend',
    value: recommend,
    value_text: 'treatment',
    message: ''
    });
}
