data.logic:
var calc_output = [];

if (UOMSYSTEM == true) {
  ht = ht * 2.54
  cr = cr
}
else {
  ht = ht
  cr = cr / 88.4
}

var gfr = 0.413 * parseFloat(ht) / cr

var stage, msg

if (gfr.toFixed(0) >= 90) {
  stage = 'Stage 1'
  msg = 'Normal'
}
else if (gfr.toFixed(0) >= 60 && gfr.toFixed(0) <= 89) {
  stage = 'Stage 2'
  msg = 'Mild CKD'
}
else if (gfr.toFixed(0) >= 45 && gfr.toFixed(0) <= 59) {
  stage = 'Stage 3a'
  msg = 'Mild-moderate CKD'
}
else if (gfr.toFixed(0) >= 30 && gfr.toFixed(0) <= 44) {
  stage = 'Stage 3b'
  msg = 'Moderate-severe CKD'
}
else if (gfr.toFixed(0) >= 15 && gfr.toFixed(0) <= 29) {
  stage = 'Stage 4'
  msg = 'Severe CKD'
}
else if (gfr.toFixed(0) < 15) {
  stage = 'Stage 5'
  msg = 'End-stage renal disease'
}

calc_output.push({
   name: 'mini',
   value: gfr.toFixed(0),
   value_text: 'mL/min/1.73 m²',
   message: ''
});
calc_output.push({
   name: '10008_Revised Schwartz Equation (2009)_result (eGFR)',
   value: gfr.toFixed(0),
   value_text: 'mL/min/1.73 m²',
   message: 'Estimated GFR'
});
calc_output.push({
   name: '10008_Revised Schwartz Equation (2009)_result (stage)',
   value: stage,
   value_text: '',
   message: msg
});
