data.logic:
var calc_output = [];

var a = parseFloat(jealousy);
var b = parseFloat(pregnancy);
var c = parseFloat(capability);
var d = parseFloat(weapon);
var e = parseFloat(increase);

var score = a + b + c + d + e; 

var risk1;

if (score < 3) {
	risk1 = 'Low';
}
else { 
	risk1 = 'High';
}


//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + ' risk of lethal or potentially lethal assault by IP'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Danger Assessment Score'   
    });
if (score < 3) {calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '',
    message: 'Risk of lethal or potentially lethal assault by IP'
});}
if (score >= 3) {calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '',
    message: 'Risk of lethal or potentially lethal assault by IP (see Evidence for details)'
});}
