var calc_output = [];

var threshold = parseFloat(threshold);
var drunk = parseFloat(drunk);
var previousep = parseFloat(previousep);
var seizures = parseFloat(seizures);
var dts = parseFloat(dts);
var rehab = parseFloat(rehab);
var blackout = parseFloat(blackout);
var downers = parseFloat(downers);
var combined = parseFloat(combined);
var bal = parseFloat(bal);
var autonomic = parseFloat(autonomic);


var risk;
var ratio;

if(threshold == 0){
        var score = 0;
    calc_output.push({
        name: 'mini',
        value: '',
        value_text: '',
        message:'Threshold criteria not met'
        });
    calc_output.push({
        name: 'score',
        value: '',
        value_text: '',
        message:'Threshold criteria not met'
        });
    }
else{
    var score = drunk + previousep + seizures +dts + rehab + blackout + downers + combined + bal + autonomic;


    if (score <4){
        risk = 'Average';
        ratio = '0.07';
    }
    else if (score >=4) {
        risk = 'High';
        ratio = '174';
    }

    //results 
    calc_output.push({
        name: 'mini',
        value: score,
        value_text: 'points',
        message: risk + ' risk'
        });
    calc_output.push({
        name: 'score',
        value: score,
        value_text: 'points',
        message: 'PAWSS'
        });
    calc_output.push({
        name: 'risk',
        value: risk, 
        value_text: 'risk',
        message: 'Likelihood ratio ' + ratio + ' for complicated AWS (withdrawal hallucinosis, withdrawal-related seizures, or delirium tremens)'
    });
}
