data.logic:
var calc_output = [];

var location = parseFloat(location);
var ovaries = parseFloat(ovaries);
var limited = parseFloat(limited);
var extension = parseFloat(extension);
var spread = parseFloat(spread);
var lymph = parseFloat(lymph);
var metastasis = parseFloat(metastasis);
var distant = parseFloat(distant);

var stage;

if (location == 0) {
  	if (ovaries == 0){
	stage = 'IA';
    }
  	else if(ovaries == 1){
	stage = 'IB';
	}
  	else if(ovaries == 2 && limited == 0){
	stage = 'IC1';
	}
 	else if(ovaries == 2 && limited == 1){
	stage = 'IC2';
	}
  	else if(ovaries == 2 && limited == 2){
	stage = 'IC3';
	}
}
else if (location == 1) {
  	if (extension == 0){
	stage = 'IIA';
    }
  	else if(extension == 1){
	stage = 'IIB';
	}
}
else if (location == 2) {
  	if (spread == 0){
		if(lymph == 0 && metastasis == 0){
          stage = 'IIIA1(i)';
   		}
      	else if (lymph == 0 && metastasis == 1){
          stage = 'IIIA1(ii)';
        }
      	else if (lymph == 1){
          stage = 'IIIA2';
        }
    }
  	else if(spread == 1){
	stage = 'IIIB';
	}
  	else if(spread == 2){
	stage = 'IIIC';
	}
}
else if (location == 3) {
  	if (distant == 0){
	stage = 'IVA';
    }
  	else if(distant == 1){
	stage = 'IVB';
	}
}

//results
calc_output.push({
    name: 'mini',
    value: 'Stage ' + stage,
    value_text: '',
    message: 'FIGO Stage'
});
calc_output.push({
    name: 'classification',
    value: 'Stage ' + stage,
    value_text: '',
    message: 'FIGO Stage'
});
